import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'prueba_tecnica_carlos';

  lista22:any[] = [];

  constructor() {
    for (let i = 0; i < 10; i++) {
      this.lista22.push({
        id: i.toString(),
        valor: i.toString(),
        localidad: 'norm',
        seleccionado: false
      });
    }
  }
}
