import { Component } from '@angular/core';

@Component({
  selector: 'app-zona-palcos',
  templateUrl: './zona-palcos.component.html',
  styleUrls: ['./zona-palcos.component.css']
})
export class ZonaPalcosComponent {

  lista22:any[] = [];

  constructor() {
    for (let i = 1; i <= 20; i++) {
      this.lista22.push({
        id: i.toString(),
        valor:i.toString(),
        localidad: 'norm',
        seleccionado: false
      });
    }
  }
  toggleSeleccion(index: number) {

    this.lista22[index].seleccionado = !this.lista22[index].seleccionado;
  }
  mostrarValor(event: MouseEvent) {
    const cuadro = event.target as HTMLElement;
    const valor = cuadro.getAttribute('data-valor');
    cuadro.style.opacity = '1'; // Muestra el valor cuando el cursor pasa sobre el cuadro

    // Puedes personalizar la posición del valor si es necesario
    cuadro.style.top = '-40px'; // Por encima del cuadro
    cuadro.style.left = '50%'; // Centrado horizontalmente
    ; // Centrado horizontalmente
  }

  ocultarValor(event: MouseEvent) {
    const cuadro = event.target as HTMLElement;
    cuadro.style.opacity = '0'; // Oculta el valor cuando el cursor se aleja
  }

}
